import type { Request } from "express";
import { UserContext } from "./jwt";
import { auditImpersonation } from "../services/admin";

export interface AdminContext extends UserContext {
  effectiveUserId: string;
  isImpersonating: boolean;
}

export async function handleAdminImpersonation(
  request: Request,
  userContext: UserContext
): Promise<AdminContext> {
  const actAsUser = request.headers['x-act-as-user'] as string;

  // Only allow impersonation for GET requests and admin users
  if (actAsUser && request.method === 'GET' && userContext.isAdmin) {
    // Audit the impersonation
    await auditImpersonation(
      userContext.userId,
      actAsUser,
      request.url,
      request.ip,
      request.headers['user-agent']
    );

    return {
      ...userContext,
      effectiveUserId: actAsUser,
      isImpersonating: true,
    };
  }

  return {
    ...userContext,
    effectiveUserId: userContext.userId,
    isImpersonating: false,
  };
}

export function requireAdmin(userContext: UserContext): void {
  if (!userContext.isAdmin) {
    throw new Error("Admin access required");
  }
}

server/auth/jwt.ts.content:
import { account } from "../config/appwrite";
import { verifyAdminStatus } from "../config/appwrite";

export interface UserContext {
  userId: string;
  isAdmin: boolean;
  profile?: any;
}

export async function verifyAppwriteJWT(jwt: string): Promise<UserContext> {
  if (!jwt) {
    throw new Error("X-Appwrite-JWT header required");
  }

  try {
    // Set the JWT session for this client
    const client = account.client.setJWT(jwt);
    const decoded = await account.get();

    // Verify admin status
    const isAdmin = await verifyAdminStatus(decoded.$id, decoded.prefs);

    return {
      userId: decoded.$id,
      isAdmin,
      profile: decoded.prefs,
    };
  } catch (error) {
    console.error("JWT verification failed:", error);
    throw new Error("Invalid or expired JWT token");
  }
}

export function extractJWTFromHeaders(headers: Record<string, string | string[] | undefined>): string | null {
  const jwt = headers['x-appwrite-jwt'];
  if (typeof jwt === 'string') {
    return jwt;
  }
  return null;
}

