import type { Request, Response, NextFunction } from "express";
import { verifyAppwriteJWT, extractJWTFromHeaders } from "../auth/jwt";
import { handleAdminImpersonation } from "../auth/admin";
import { setCurrentUser } from "../config/database";

declare global {
  namespace Express {
    interface Request {
      user?: any;
    }
  }
}

export async function authMiddleware(req: Request, res: Response, next: NextFunction) {
  try {
    console.log(`[AUTH] Processing request: ${req.url}, NODE_ENV: ${process.env.NODE_ENV}, includes admin: ${req.url.includes('/admin')}`);

    // Development bypass for admin routes
    if (process.env.NODE_ENV === 'development' && req.url.includes('/admin')) {
      console.log(`[AUTH] Development bypass activated for: ${req.url}`);
      req.user = {
        userId: 'dev-admin-user',
        isAdmin: true,
        effectiveUserId: 'dev-admin-user',
        isImpersonating: false,
        profile: { role: 'admin' }
      };
      await setCurrentUser('dev-admin-user');
      return next();
    }

    const jwt = extractJWTFromHeaders(req.headers);

    if (!jwt) {
      return res.status(401).json({
        type: 'about:blank',
        title: 'Unauthorized',
        status: 401,
        detail: 'X-Appwrite-JWT header required',
        instance: req.url
      });
    }

    // Verify JWT and get user context
    const userContext = await verifyAppwriteJWT(jwt);

    // Handle admin impersonation
    const adminContext = await handleAdminImpersonation(req, userContext);

    // Set current user for RLS
    await setCurrentUser(adminContext.effectiveUserId);

    // Attach to request
    req.user = adminContext;

    next();
  } catch (error: any) {
    res.status(401).json({
      type: 'about:blank',
      title: 'Unauthorized',
      status: 401,
      detail: error.message || 'Authentication failed',
      instance: req.url
    });
  }
}

export function optionalAuth(req: Request, res: Response, next: NextFunction) {
  const jwt = extractJWTFromHeaders(req.headers);

  if (!jwt) {
    return next();
  }

  authMiddleware(req, res, next);
}

server/middleware/errorHandler.ts.content:
import type { Request, Response, NextFunction } from "express";

export interface ProblemDetail {
  type: string;
  title: string;
  status: number;
  detail: string;
  instance: string;
  [key: string]: any;
}

export class AppError extends Error {
  constructor(
    public status: number,
    public title: string,
    public detail: string,
    public type: string = 'about:blank',
    public extra?: Record<string, any>
  ) {
    super(detail);
    this.name = 'AppError';
  }
}

export function errorHandler(err: any, req: Request, res: Response, next: NextFunction) {
  // If headers already sent, delegate to default Express error handler
  if (res.headersSent) {
    return next(err);
  }

  console.error('Error:', err);

  let problemDetail: ProblemDetail;

  if (err instanceof AppError) {
    problemDetail = {
      type: err.type,
      title: err.title,
      status: err.status,
      detail: err.detail,
      instance: req.url,
      ...err.extra,
    };
  } else if (err.name === 'ValidationError' || err.validation) {
    problemDetail = {
      type: 'https://nutrition-app.com/validation-error',
      title: 'Validation Error',
      status: 400,
      detail: 'Request validation failed',
      instance: req.url,
      errors: err.validation || err.details,
    };
  } else if (err.code === '23505') { // PostgreSQL unique violation
    problemDetail = {
      type: 'https://nutrition-app.com/duplicate-resource',
      title: 'Duplicate Resource',
      status: 409,
      detail: 'A resource with this identifier already exists',
      instance: req.url,
    };
  } else if (err.code === '23503') { // PostgreSQL foreign key violation
    problemDetail = {
      type: 'https://nutrition-app.com/reference-error',
      title: 'Reference Error',
      status: 400,
      detail: 'Referenced resource does not exist',
      instance: req.url,
    };
  } else {
    problemDetail = {
      type: 'about:blank',
      title: 'Internal Server Error',
      status: 500,
      detail: 'An unexpected error occurred',
      instance: req.url,
    };
  }

  res
    .status(problemDetail.status)
    .type('application/problem+json')
    .json(problemDetail);
}

export function notFoundHandler(req: Request, res: Response) {
  res.status(404).json({
    type: 'about:blank',
    title: 'Not Found',
    status: 404,
    detail: `No resource found at ${req.path}`,
    instance: req.url,
  });
}

server/middleware/idempotency.ts.content:
import type { Request, Response, NextFunction } from "express";
import { createHash } from "crypto";
import { db } from "../config/database";
import { idempotencyKeys } from "@shared/schema";
import { eq, and } from "drizzle-orm";

export async function idempotencyMiddleware(req: Request, res: Response, next: NextFunction) {
  const idempotencyKey = req.headers['idempotency-key'] as string;

  // Require idempotency key for state-changing operations
  if (['POST', 'PUT', 'PATCH'].includes(req.method) && !idempotencyKey) {
    return res.status(400).json({
      type: 'about:blank',
      title: 'Bad Request',
      status: 400,
      detail: 'Idempotency-Key header required for state-changing operations',
      instance: req.url
    });
  }

  if (!idempotencyKey) {
    return next();
  }

  try {
    // Create request hash
    const requestHash = createHash('sha256')
      .update(JSON.stringify(req.body || {}))
      .digest('hex');

    // Check for existing request
    const existing = await db
      .select()
      .from(idempotencyKeys)
      .where(
        and(
          eq(idempotencyKeys.key, idempotencyKey),
          eq(idempotencyKeys.method, req.method),
          eq(idempotencyKeys.path, req.path)
        )
      )
      .limit(1);

    if (existing.length > 0) {
      const record = existing[0];

      // Check if request body is different
      if (record.requestHash !== requestHash) {
        return res.status(409).json({
          type: 'about:blank',
          title: 'Conflict',
          status: 409,
          detail: 'Idempotency key reused with different request body',
          instance: req.url
        });
      }

      // Return cached response if already processed
      if (record.responseStatus && record.responseBody) {
        return res.status(record.responseStatus).json(record.responseBody);
      }
    } else {
      // Store new idempotency key
      await db.insert(idempotencyKeys).values({
        key: idempotencyKey,
        method: req.method,
        path: req.path,
        requestHash,
      });
    }

    // Store idempotency info for response handling
    res.locals.idempotencyKey = idempotencyKey;

    next();
  } catch (error) {
    console.error("Idempotency middleware error:", error);
    next();
  }
}

// Middleware to store response for idempotency
export function storeIdempotentResponse(req: Request, res: Response, next: NextFunction) {
  const originalJson = res.json;
  const idempotencyKey = res.locals.idempotencyKey;

  if (idempotencyKey && ['POST', 'PUT', 'PATCH'].includes(req.method)) {
    res.json = function(body: any) {
      // Store the response
      db.update(idempotencyKeys)
        .set({
          responseStatus: res.statusCode,
          responseBody: body,
          processedAt: new Date(),
        })
        .where(eq(idempotencyKeys.key, idempotencyKey))
        .catch(err => console.error("Failed to store idempotent response:", err));

      return originalJson.call(this, body);
    };
  }

  next();
}

server/middleware/rateLimit.ts.content:
import type { Request, Response, NextFunction } from "express";
import { env } from "../config/env";

// In-memory rate limiting store (use Redis in production)
const rateLimitStore = new Map<string, { count: number; resetTime: number }>();

export async function rateLimitMiddleware(req: Request, res: Response, next: NextFunction) {
  if (!req.user) {
    return next(); // Skip rate limiting for unauthenticated requests
  }

  const userId = req.user.userId;
  const isHeavyWrite = ['POST', 'PUT', 'PATCH', 'DELETE'].includes(req.method);
  const limit = isHeavyWrite ? env.RATE_LIMITS_WRITE_RPM : env.RATE_LIMITS_READ_RPM;
  const windowMs = 60 * 1000; // 1 minute window

  const bucket = `${userId}:${isHeavyWrite ? 'write' : 'read'}`;
  const now = Date.now();
  const windowStart = now - windowMs;

  // Get current bucket state
  let bucketState = rateLimitStore.get(bucket);

  // Reset if window has passed
  if (!bucketState || bucketState.resetTime <= now) {
    bucketState = {
      count: 0,
      resetTime: now + windowMs
    };
  }

  // Increment counter
  bucketState.count++;
  rateLimitStore.set(bucket, bucketState);

  // Calculate remaining requests
  const remaining = Math.max(0, limit - bucketState.count);
  const resetTime = new Date(bucketState.resetTime);

  // Set rate limit headers
  res.setHeader('RateLimit', remaining.toString());
  res.setHeader('RateLimit-Policy', `${limit};w=60`);
  res.setHeader('RateLimit-Reset', resetTime.toISOString());

  // Check if limit exceeded
  if (bucketState.count > limit) {
    return res.status(429).json({
      type: 'about:blank',
      title: 'Too Many Requests',
      status: 429,
      detail: `Rate limit exceeded. ${limit} requests per minute allowed.`,
      instance: req.url
    });
  }

  next();
}

// Cleanup old entries periodically
setInterval(() => {
  const now = Date.now();
  for (const [key, bucket] of rateLimitStore.entries()) {
    if (bucket.resetTime <= now) {
      rateLimitStore.delete(key);
    }
  }
}, 5 * 60 * 1000); // Cleanup every 5 minutes

server/routes/admin.ts.content:
import { Router } from "express";
import { z } from "zod";
import { authMiddleware } from "../middleware/auth";
import { rateLimitMiddleware } from "../middleware/rateLimit";
import { auditedRoute } from "../middleware/audit";
import { requireAdmin } from "../auth/admin";
import { setCurrentUser } from "../config/database";
import { 
  createCuratedRecipe, 
  updateCuratedRecipe, 
  deleteCuratedRecipe,
  getReports,
  resolveReport,
  getAuditLog,
  refreshMaterializedViews,
  getDashboardStats
} from "../services/admin";
import { approveUserRecipe, rejectUserRecipe } from "../services/userContent";
import { insertRecipeSchema } from "@shared/schema";

const router = Router();

// Development bypass for all admin routes
if (process.env.NODE_ENV === 'development') {
  router.use(async (req, res, next) => {
    console.log(`[ADMIN] Development bypass for: ${req.url}`);
    req.user = {
      userId: 'dev-admin-user',
      isAdmin: true,
      effectiveUserId: 'dev-admin-user',
      isImpersonating: false,
      profile: { role: 'admin' }
    };
    await setCurrentUser('dev-admin-user');
    next();
  });
} else {
  // Require admin for all routes in production
  router.use(authMiddleware);
  router.use((req, res, next) => {
    requireAdmin(req.user);
    next();
  });
}
router.use(rateLimitMiddleware);

// Dashboard
router.get("/dashboard", async (req, res, next) => {
  try {
    const stats = await getDashboardStats();
    res.json(stats);
  } catch (error) {
    next(error);
  }
});

// Curated recipe management
router.post("/recipes", auditedRoute(async (req, res, next) => {
  try {
    const recipeData = insertRecipeSchema.parse(req.body);
    const recipe = await createCuratedRecipe(req.user.userId, recipeData, req.body.reason);
    res.status(201).json(recipe);
  } catch (error) {
    next(error);
  }
}));

router.put("/recipes/:id", auditedRoute(async (req, res, next) => {
  try {
    const updates = insertRecipeSchema.partial().parse(req.body);
    const recipe = await updateCuratedRecipe(req.user.userId, req.params.id, updates, req.body.reason);
    res.json(recipe);
  } catch (error) {
    next(error);
  }
}));

router.delete("/recipes/:id", auditedRoute(async (req, res, next) => {
  try {
    const { reason } = req.body;
    if (!reason) {
      return res.status(400).json({
        type: 'about:blank',
        title: 'Bad Request',
        status: 400,
        detail: 'Reason is required for recipe deletion',
        instance: req.url
      });
    }

    const result = await deleteCuratedRecipe(req.user.userId, req.params.id, reason);
    res.json(result);
  } catch (error) {
    next(error);
  }
}));

// User content moderation
router.post("/user-recipes/:id/approve", auditedRoute(async (req, res, next) => {
  try {
    const { reviewNotes } = req.body;
    const recipe = await approveUserRecipe(req.user.userId, req.params.id, reviewNotes);
    res.json(recipe);
  } catch (error) {
    next(error);
  }
}));

router.post("/user-recipes/:id/reject", auditedRoute(async (req, res, next) => {
  try {
    const { reviewNotes } = req.body;
    if (!reviewNotes) {
      return res.status(400).json({
        type: 'about:blank',
        title: 'Bad Request',
        status: 400,
        detail: 'Review notes are required for rejection',
        instance: req.url
      });
    }

    const recipe = await rejectUserRecipe(req.user.userId, req.params.id, reviewNotes);
    res.json(recipe);
  } catch (error) {
    next(error);
  }
}));

// Reports and moderation
router.get("/reports", async (req, res, next) => {
  try {
    const status = req.query.status as string;
    const limit = req.query.limit ? parseInt(req.query.limit as string) : 50;
    const offset = req.query.offset ? parseInt(req.query.offset as string) : 0;

    const reports = await getReports(status, limit, offset);
    res.json(reports);
  } catch (error) {
    next(error);
  }
});

router.post("/reports/:id/resolve", auditedRoute(async (req, res, next) => {
  try {
    const { action, reason, notes } = req.body;

    const schema = z.object({
      action: z.enum(['dismiss', 'remove_content', 'warn_user', 'ban_user']),
      reason: z.string().min(1),
      notes: z.string().optional(),
    });

    const validated = schema.parse({ action, reason, notes });

    const resolution = await resolveReport(
      req.user.userId,
      req.params.id,
      validated.action,
      validated.reason,
      validated.notes
    );

    res.json(resolution);
  } catch (error) {
    next(error);
  }
}));

// Audit logs
router.get("/audit", async (req, res, next) => {
  try {
    const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
    const offset = req.query.offset ? parseInt(req.query.offset as string) : 0;
    const actorUserId = req.query.actor_user_id as string;

    const logs = await getAuditLog(limit, offset, actorUserId);
    res.json(logs);
  } catch (error) {
    next(error);
  }
});

// System operations
router.post("/refresh-materialized-views", auditedRoute(async (req, res, next) => {
  try {
    const result = await refreshMaterializedViews();
    res.json(result);
  } catch (error) {
    next(error);
  }
}));

export default router;

