server/services/admin.ts.content:
import { db, executeRaw } from "../config/database";
import { recipes, userRecipes, recipeReports, recipeReportResolutions, auditLog } from "@shared/schema";
import { eq, desc, and } from "drizzle-orm";
import type { InsertRecipe } from "@shared/schema";
import { auditLogEntry } from "../middleware/audit";

export async function createCuratedRecipe(adminUserId: string, recipeData: InsertRecipe, reason?: string) {
  const recipe = await db.insert(recipes).values({
    ...recipeData,
    status: 'published',
    publishedAt: new Date(),
  }).returning();

  await auditLogEntry(
    adminUserId,
    'CREATE_CURATED_RECIPE',
    'recipes',
    recipe[0].id,
    null,
    recipe[0],
    reason
  );

  return recipe[0];
}

export async function updateCuratedRecipe(
  adminUserId: string,
  recipeId: string,
  updates: Partial<InsertRecipe>,
  reason?: string
) {
  // Get before state
  const before = await db
    .select()
    .from(recipes)
    .where(eq(recipes.id, recipeId))
    .limit(1);

  if (before.length === 0) {
    throw new Error("Recipe not found");
  }

  const updated = await db
    .update(recipes)
    .set({
      ...updates,
      updatedAt: new Date(),
    })
    .where(eq(recipes.id, recipeId))
    .returning();

  await auditLogEntry(
    adminUserId,
    'UPDATE_CURATED_RECIPE',
    'recipes',
    recipeId,
    before[0],
    updated[0],
    reason
  );

  return updated[0];
}

export async function deleteCuratedRecipe(adminUserId: string, recipeId: string, reason: string) {
  // Get before state
  const before = await db
    .select()
    .from(recipes)
    .where(eq(recipes.id, recipeId))
    .limit(1);

  if (before.length === 0) {
    throw new Error("Recipe not found");
  }

  await db.delete(recipes).where(eq(recipes.id, recipeId));

  await auditLogEntry(
    adminUserId,
    'DELETE_CURATED_RECIPE',
    'recipes',
    recipeId,
    before[0],
    null,
    reason
  );

  return { success: true };
}

export async function getReports(status?: string, limit: number = 50, offset: number = 0) {
  let query = db
    .select({
      report: recipeReports,
      recipe: recipes,
      userRecipe: userRecipes,
    })
    .from(recipeReports)
    .leftJoin(recipes, eq(recipeReports.recipeId, recipes.id))
    .leftJoin(userRecipes, eq(recipeReports.userRecipeId, userRecipes.id));

  if (status) {
    query = query.where(eq(recipeReports.status, status));
  }

  const results = await query
    .orderBy(desc(recipeReports.createdAt))
    .limit(limit)
    .offset(offset);

  return results;
}

export async function resolveReport(
  adminUserId: string,
  reportId: string,
  action: string,
  reason: string,
  notes?: string
) {
  // Update report status
  await db
    .update(recipeReports)
    .set({
      status: 'resolved',
      updatedAt: new Date(),
    })
    .where(eq(recipeReports.id, reportId));

  // Create resolution record
  const resolution = await db.insert(recipeReportResolutions).values({
    reportId,
    resolvedBy: adminUserId,
    action,
    reason,
    notes,
  }).returning();

  await auditLogEntry(
    adminUserId,
    'RESOLVE_REPORT',
    'recipe_reports',
    reportId,
    null,
    { action, reason, notes }
  );

  return resolution[0];
}

export async function getAuditLog(limit: number = 100, offset: number = 0, actorUserId?: string) {
  let query = db.select().from(auditLog);

  if (actorUserId) {
    query = query.where(eq(auditLog.actorUserId, actorUserId));
  }

  const logs = await query
    .orderBy(desc(auditLog.at))
    .limit(limit)
    .offset(offset);

  return logs;
}

export async function auditImpersonation(
  adminUserId: string,
  targetUserId: string,
  url: string,
  ip?: string,
  userAgent?: string
) {
  await auditLogEntry(
    adminUserId,
    'USER_IMPERSONATION',
    'users',
    targetUserId,
    null,
    { url, ip, userAgent },
    'Admin impersonation for support/debugging'
  );
}

export async function refreshMaterializedViews(): Promise<{ success: boolean; duration: number }> {
  const start = Date.now();

  try {
    await executeRaw('REFRESH MATERIALIZED VIEW CONCURRENTLY mv_recipe_popularity_30d');
    const duration = Date.now() - start;

    return { success: true, duration };
  } catch (error) {
    console.error("Failed to refresh materialized views:", error);
    throw new Error("Materialized view refresh failed");
  }
}

export async function getDashboardStats() {
  try {
    const [totalRecipes, activeUsers, searchQps, pendingReview] = await Promise.all([
      db.select().from(recipes).where(eq(recipes.status, 'published')),
      executeRaw('SELECT COUNT(DISTINCT user_id) as count FROM recipe_history WHERE at > NOW() - INTERVAL \'30 days\''),
      executeRaw('SELECT COUNT(*) as count FROM recipe_history WHERE event = \'viewed\' AND at > NOW() - INTERVAL \'1 minute\''),
      db.select().from(userRecipes).where(eq(userRecipes.reviewStatus, 'pending')),
    ]);

    return {
      totalRecipes: totalRecipes.length,
      activeUsers: activeUsers[0]?.count || 0,
      searchQps: searchQps[0]?.count || 0,
      pendingReview: pendingReview.length,
    };
  } catch (error) {
    console.error("Dashboard stats error:", error);
    throw new Error("Failed to get dashboard stats");
  }
}

server/services/feed.ts.content:
import { db, executeRaw } from "../config/database";
import { userProfiles, recipes } from "@shared/schema";
import { eq } from "drizzle-orm";

export interface FeedResult {
  recipe: any;
  score: number;
  reasons: string[];
}

export async function getPersonalizedFeed(
  userId: string,
  limit: number = 50,
  offset: number = 0
): Promise<FeedResult[]> {
  try {
    const results = await executeRaw(`
      SELECT * FROM personalized_feed($1, $2, $3)
    `, [userId, limit, offset]);

    return results.map((row: any) => ({
      recipe: row.recipe,
      score: parseFloat(row.score),
      reasons: row.reasons || []
    }));
  } catch (error) {
    console.error("Personalized feed error:", error);
    throw new Error("Failed to generate personalized feed");
  }
}

export async function getUserProfile(userId: string) {
  const profile = await db
    .select()
    .from(userProfiles)
    .where(eq(userProfiles.userId, userId))
    .limit(1);

  return profile[0] || null;
}

export async function createOrUpdateUserProfile(userId: string, profileData: any) {
  const existing = await getUserProfile(userId);

  if (existing) {
    await db
      .update(userProfiles)
      .set({
        ...profileData,
        updatedAt: new Date(),
      })
      .where(eq(userProfiles.userId, userId));
  } else {
    await db.insert(userProfiles).values({
      userId,
      ...profileData,
    });
  }

  return getUserProfile(userId);
}

export async function getFeedRecommendations(userId: string): Promise<{
  trending: any[];
  forYou: FeedResult[];
  recent: any[];
}> {
  try {
    // Get trending recipes (popular in last 7 days)
    const trending = await executeRaw(`
      SELECT r.*, COUNT(rh.id) as recent_activity
      FROM recipes r
      LEFT JOIN recipe_history rh ON r.id = rh.recipe_id 
        AND rh.event = 'cooked' 
        AND rh.at > NOW() - INTERVAL '7 days'
      WHERE r.status = 'published' AND r.market_country = 'US'
      GROUP BY r.id
      ORDER BY recent_activity DESC, r.updated_at DESC
      LIMIT 10
    `);

    // Get personalized recommendations
    const forYou = await getPersonalizedFeed(userId, 20);

    // Get recently published recipes
    const recent = await db
      .select()
      .from(recipes)
      .where(eq(recipes.status, "published"))
      .orderBy(recipes.publishedAt)
      .limit(10);

    return {
      trending,
      forYou,
      recent,
    };
  } catch (error) {
    console.error("Feed recommendations error:", error);
    throw new Error("Failed to get feed recommendations");
  }
}

server/services/recipes.ts.content:
import { db } from "../config/database";
import { recipes, savedRecipes, recipeHistory, userRecipes } from "@shared/schema";
import { eq, and, desc } from "drizzle-orm";
import type { InsertRecipeHistory } from "@shared/schema";

export async function toggleSaveRecipe(userId: string, recipeId: string): Promise<{ saved: boolean }> {
  // Check if already saved
  const existing = await db
    .select()
    .from(savedRecipes)
    .where(and(
      eq(savedRecipes.userId, userId),
      eq(savedRecipes.recipeId, recipeId)
    ))
    .limit(1);

  if (existing.length > 0) {
    // Remove save
    await db
      .delete(savedRecipes)
      .where(and(
        eq(savedRecipes.userId, userId),
        eq(savedRecipes.recipeId, recipeId)
      ));
    return { saved: false };
  } else {
    // Add save
    await db.insert(savedRecipes).values({
      userId,
      recipeId,
    });
    return { saved: true };
  }
}

export async function getSavedRecipes(userId: string, limit: number = 50, offset: number = 0) {
  const saved = await db
    .select({
      recipe: recipes,
      savedAt: savedRecipes.savedAt,
    })
    .from(savedRecipes)
    .innerJoin(recipes, eq(savedRecipes.recipeId, recipes.id))
    .where(eq(savedRecipes.userId, userId))
    .orderBy(desc(savedRecipes.savedAt))
    .limit(limit)
    .offset(offset);

  return saved;
}

export async function logRecipeHistory(historyData: InsertRecipeHistory): Promise<void> {
  // Check for throttling on 'viewed' events (max 1 per hour per recipe/user)
  if (historyData.event === 'viewed') {
    const recentView = await db
      .select()
      .from(recipeHistory)
      .where(and(
        eq(recipeHistory.userId, historyData.userId),
        eq(recipeHistory.recipeId, historyData.recipeId),
        eq(recipeHistory.event, 'viewed')
      ))
      .orderBy(desc(recipeHistory.at))
      .limit(1);

    if (recentView.length > 0) {
      const hourAgo = new Date(Date.now() - 60 * 60 * 1000);
      if (recentView[0].at > hourAgo) {
        // Skip logging - too recent
        return;
      }
    }
  }

  await db.insert(recipeHistory).values(historyData);
}

export async function getRecipeHistory(
  userId: string,
  event?: string,
  limit: number = 50,
  offset: number = 0
) {
  let query = db
    .select({
      history: recipeHistory,
      recipe: recipes,
    })
    .from(recipeHistory)
    .innerJoin(recipes, eq(recipeHistory.recipeId, recipes.id))
    .where(eq(recipeHistory.userId, userId));

  if (event) {
    query = query.where(and(
      eq(recipeHistory.userId, userId),
      eq(recipeHistory.event, event)
    ));
  }

  const results = await query
    .orderBy(desc(recipeHistory.at))
    .limit(limit)
    .offset(offset);

  return results;
}

export async function getRecentlyViewed(userId: string, limit: number = 20) {
  return getRecipeHistory(userId, 'viewed', limit);
}

export async function getMostCooked(userId: string, limit: number = 20) {
  const results = await db
    .select({
      recipe: recipes,
      cookCount: recipeHistory.id, // Will be aggregated
    })
    .from(recipeHistory)
    .innerJoin(recipes, eq(recipeHistory.recipeId, recipes.id))
    .where(and(
      eq(recipeHistory.userId, userId),
      eq(recipeHistory.event, 'cooked')
    ))
    .groupBy(recipes.id)
    .orderBy(desc(recipeHistory.id))
    .limit(limit);

  return results;
}

export async function getSharedRecipe(shareSlug: string) {
  const shared = await db
    .select()
    .from(userRecipes)
    .where(and(
      eq(userRecipes.shareSlug, shareSlug),
      eq(userRecipes.visibility, 'shared')
    ))
    .limit(1);

  if (shared.length === 0) {
    throw new Error("Shared recipe not found");
  }

  return shared[0];
}

